import React, { useState } from 'react';

const WeatherApp = () => {
    const [service, setService] = useState('');
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState(null);

    const fetchWeather = async () => {
        try {
            const response = await fetch(`/weather/${service}/${city}`);
            const data = await response.json();
            setWeather(data);
        } catch (error) {
            console.error('Error fetching weather data:', error);
        }
    };

    return (
        <form>
            <input type="text" value={service} onChange={(e) => setService(e.target.value)}
                   placeholder="Enter service"  defaultValue="openweathermap"/>
            <input type="text" value={city} onChange={(e) => setCity(e.target.value)}
                   placeholder="Enter city"/>
            <button onClick={fetchWeather}>Get Weather</button>
            {weather && (
                <div>
                    <h2>{weather.name || 'Unknown city'}</h2>
                    <p>Temperature: {weather.main ? weather.main.temp : 0} °C</p>
                    <p>Humidity: {weather.main ? weather.main.humidity : 0}%</p>
                </div>
            )}
        </form>
    );
};

export default WeatherApp;
