import React, { useState } from 'react';

const WeatherApp = () => {
    const [service, setService] = useState('');
    const [city, setCity] = useState('');
    const [weather, setWeather] = useState(null);
    const [error, setError] = useState('');

    const fetchWeather = async (e) => {
        e.preventDefault();

        if (!service || !city) {
            setError('Please, fill in both service and city fields.');
            return;
        }

        try {
            const response = await fetch(`/weather/${service}/${city}`);
            if (!response.ok) {
                throw new Error('Failed to fetch weather data.');
            }
            const data = await response.json();
            setWeather(data);
            setError('');

            // Получаем URL изображения города
            const imageUrl = data.hits.length > 0 ? data.hits[0].pageURL : '';
            setWeather(prevState => ({ ...prevState, imageUrl: imageUrl }));
        } catch (error) {
            console.error('Error fetching weather data:', error);
            setError('Error fetching service or city data. Please try again.');
        }
    };

    return (
        <>
            <form onSubmit={fetchWeather}>
                <input type="text" value={service} onChange={(e) => setService(e.target.value)}
                       placeholder="Enter service"/>
                <input type="text" value={city} onChange={(e) => setCity(e.target.value)}
                       placeholder="Enter city"/>
                <button type="submit">Get Weather</button>
            </form>
            {error && <p>{error}</p>}
            {weather && (
                <div>
                    <h2>{weather.name || 'Unknown city'}</h2>
                    <img src={weather.imageUrl} alt="City" />
                    <p>Temperature: {weather.main ? weather.main.temp : 0} °C</p>
                    <p>Humidity: {weather.main ? weather.main.humidity : 0}%</p>
                </div>
            )}
        </>
    );
};

export default WeatherApp;
